CREATE VIEW [slc].[vw_folder_by_size]
AS
SELECT     f.FolderID, f.ParentFolderID, f.ServerID, SUM(ISNULL(fi.SizeBytes, 0)) AS SizeBytes, f.[Level], f.Name, f.DriveID
FROM         slc.Folder AS f LEFT OUTER JOIN
                      slc.FileName AS fi ON f.FolderID = fi.FolderID
GROUP BY f.FolderID, f.ParentFolderID, f.ServerID, f.[Level], f.Name, f.DriveID

