Create view [slc].[vw_FolderHierarchySize]
  as
  
  
   with [cte_folders] (
  [FolderID],[ParentFolderID],[ServerID],[SizeBytes],[Level])
  as (
  SELECT  [FolderID],[ParentFolderID],[ServerID],[SizeBytes], 0 as level
  FROM [slc].[vw_folder_by_size] where [ParentFolderID]=-1
  
  union all
  
  SELECT  pn.[FolderID],pn.[ParentFolderID],pn.[ServerID],isnull(pn.[SizeBytes],0)+ isnull(pn.[SizeBytes],0),p1.Level + 1
  from [slc].[vw_folder_by_size] as pn
  inner join [cte_folders] as p1
  on p1.FolderID=pn.ParentFolderID
  )
  --select * from [cte_folders]
  
  select c1.[FolderID],c1.[ParentFolderID],c1.[ServerID],c1.[Level] 
  ,c1.[SizeBytes]
  --,c2.[SizeBytes] --+ 
  ,SUM(isnull(c2.[SizeBytes],0)) + isnull(c1.[SizeBytes],0) as total
  from [cte_folders] c1 
  left outer join [cte_folders] c2 on 
  c1.[FolderID]=c2.[ParentFolderID]
  --where c2.[ParentFolderID]=1904
  group by c1.[FolderID],c1.[ParentFolderID],c1.[ServerID],c1.[Level],c1.[SizeBytes]
