	select @lev2 =SUBSTRING(@path,CHARINDEX('\',@path,CHARINDEX('\',@path,1)+1)+1,len(@path)- CHARINDEX('\',@path,CHARINDEX('\',@path,1)+1))



--select * from slc.folder where folderid=291698



