CREATE  PROCEDURE [slc].[GetFolderHierarchy](@folderID int,@sort char(4)='asc')
AS
BEGIN

if (@sort = 'asc')
begin
;with cte ([FolderID],[ParentFolderID],[ServerID],[SizeBytes],[Level],[Name],[DriveID],RelLevel) as
  (
	SELECT [FolderID],[ParentFolderID],[ServerID],[SizeBytes],[Level],[Name],[DriveID],0 as Level2
	  FROM [ITInfra].[slc].[vw_folder_by_size] where folderid=@folderID
	  union all
	  SELECT  t1.[FolderID],t1.[ParentFolderID],t1.[ServerID],t1.[SizeBytes],t1.[Level],t1.[Name],t1.[DriveID],t2.RelLevel + 1 as Level2
	  FROM [ITInfra].[slc].[vw_folder_by_size] t1
	  inner join cte t2
	  on t1.ParentFolderID=t2.FolderID
  )
  select 
	t1.[FolderID],t1.[ParentFolderID],t1.[ServerID],t1.[SizeBytes],t1.[Level]
	,'G:' +substring(t1.[Name],charindex('\',t1.[Name],charindex('\',t1.[Name],1)+1),len(t1.[Name])-charindex('\',t1.[Name],charindex('\',t1.[Name],1))) as Name
	,t1.[DriveID],t1.RelLevel
	,count(t2.sizeBytes) as [No.Files]
	,AVG(t2.sizeBytes)/(1024) as averagesizeKB 
	,cast(min(t2.[CreationTime]) as date) as MinCreationTime
	,cast(max(t2.[CreationTime]) as date) as MaxCreationTime
	,cast(min(t2.[LastWriteTime]) as date) as MinLastWriteTime
	,cast(max(t2.[LastWriteTime]) as date) as MaxLastWriteTime
	,cast(min(t2.[LastAccessTime]) as date) as MinLastAccessTime
	,cast(max(t2.[LastAccessTime]) as date) as MaxLastAccessTime
	,t3.percentage
	,t3.owner
	,t3.rnk
 from 
	cte t1 left outer join slc.FileName t2 
 on 
	t1.[FolderID] = t2.[FolderID]
left outer join (select *from slc.TopFileOwners where rnk=1) t3
--inner join slc.vw_TopFileOwner t3
on 
	t1.[FolderID] = t3.[FolderID]
group by 
t1.[FolderID],t1.[ParentFolderID],t1.[ServerID],t1.[SizeBytes],t1.[Level]
	,'G:' +substring(t1.[Name],charindex('\',t1.[Name],charindex('\',t1.[Name],1)+1),len(t1.[Name])-charindex('\',t1.[Name],charindex('\',t1.[Name],1)))
	,t1.[DriveID],t1.RelLevel
	, t3.owner
	,t3.percentage
	,t3.rnk
  
end
else
begin
;with cte ([FolderID],[ParentFolderID],[ServerID],[SizeBytes],[Level],[Name],[DriveID],RelLevel) as
  (
	SELECT [FolderID],[ParentFolderID],[ServerID],[SizeBytes],[Level],[Name],[DriveID],0 as Level2
	  FROM [ITInfra].[slc].[vw_folder_by_size] where folderid=@folderID
	  union all
	  SELECT  t1.[FolderID],t1.[ParentFolderID],t1.[ServerID],t1.[SizeBytes],t1.[Level],t1.[Name],t1.[DriveID],t2.RelLevel + 1 as Level2
	  FROM [ITInfra].[slc].[vw_folder_by_size] t1
	  inner join cte t2
	  on t1.FolderID=t2.ParentFolderID
  )
  select 
	t1.[FolderID],t1.[ParentFolderID],t1.[ServerID],t1.[SizeBytes],t1.[Level]
	,'G:' +substring(t1.[Name],charindex('\',t1.[Name],charindex('\',t1.[Name],1)+1),len(t1.[Name])-charindex('\',t1.[Name],charindex('\',t1.[Name],1))) as Name
	,t1.[DriveID],t1.RelLevel, 
	count(t2.sizeBytes) as [No.Files]
	,AVG(t2.sizeBytes)/(1024) as averagesizeKB
	,cast(min(t2.[CreationTime]) as date) as MinCreationTime
	,cast(max(t2.[CreationTime]) as date) as MaxCreationTime
	,cast(min(t2.[LastWriteTime]) as date) as MinLastWriteTime
	,cast(max(t2.[LastWriteTime]) as date) as MaxLastWriteTime
	,cast(min(t2.[LastAccessTime]) as date) as MinLastAccessTime
	,cast(max(t2.[LastAccessTime]) as date) as MaxLastAccessTime
	
	,t3.percentage
	,t3.owner
	,t3.rnk
   from 
	cte t1 left outer join slc.FileName t2 
 on 
	t1.[FolderID] = t2.[FolderID]
left outer join (select *from slc.TopFileOwners where rnk=1) t3
--inner join slc.vw_TopFileOwner t3
on 
	t1.[FolderID] = t3.[FolderID]

group by
t1.[FolderID],t1.[ParentFolderID],t1.[ServerID],t1.[SizeBytes],t1.[Level]
	,'G:' +substring(t1.[Name],charindex('\',t1.[Name],charindex('\',t1.[Name],1)+1),len(t1.[Name])-charindex('\',t1.[Name],charindex('\',t1.[Name],1))) 
	,t1.[DriveID],t1.RelLevel
	, t3.owner
	,t3.percentage
	,t3.rnk

end

