CREATE FUNCTION [slc].[udf_Int8_to_DateTime](
   @Int8 BIGINT
)
RETURNS DATETIME2
AS
   BEGIN
      RETURN (DATEADD(mi,case when @Int8 / 600000000 + DATEDIFF(Minute,GetUTCDate(),GetDate()) >2147483647 then 2147483647 else  @Int8 / 600000000 + DATEDIFF(Minute,GetUTCDate(),GetDate()) end,CAST('1/1/1601' AS DATETIME2)))
   END
